    private void scrollAdjustmentValueChanged(java.awt.event.AdjustmentEvent evt) {//GEN-FIRST:event_scrollAdjustmentValueChanged
        // TODO add your handling code here:
        int valorScroll = scroll.getValue();
        
        if (valorScroll > 1 && valorScroll <=31){
            dia.select(valorScroll);
            mes.select(0);
        }
        
        if (valorScroll > 31 && valorScroll <=59){
            dia.select(valorScroll-31);            
            mes.select(1);
            
        } 
        
        if (valorScroll > 59 && valorScroll <=90){
            dia.select(valorScroll-59);
            mes.select(2);
        } 
        
        if (valorScroll > 90 && valorScroll <=120){
            dia.select(valorScroll-90);
            mes.select(3);
        } 
        
        if (valorScroll > 120 && valorScroll <=151){
            dia.select(valorScroll-120);
            mes.select(4);
        } 
        
        if (valorScroll > 151 && valorScroll <=181){
            dia.select(valorScroll-151);
            mes.select(5);
        } 
        
        if (valorScroll > 181 && valorScroll <=212){
            dia.select(valorScroll-181);
            mes.select(6);
        } 
        
        if (valorScroll > 212 && valorScroll <=243){
            dia.select(valorScroll-212);
            mes.select(7);
        } 
        
        if (valorScroll > 243 && valorScroll <=273){
            dia.select(valorScroll-243);
            mes.select(8);
        } 
        
        if (valorScroll > 273 && valorScroll <=304){
            dia.select(valorScroll-273);
            mes.select(9);
        } 
        
        if (valorScroll > 304 && valorScroll <=343){
            dia.select(valorScroll-304);
            mes.select(10);
        } 
        
        if (valorScroll > 334 && valorScroll <=365){
            dia.select(valorScroll-334);
            mes.select(11);
        } 
        
    }//GEN-LAST:event_scrollAdjustmentValueChanged

